-- Create "departments" table
CREATE TABLE `departments` (`id` varchar(255) NOT NULL, `created_at` timestamp NOT NULL, `updated_at` timestamp NOT NULL, `is_enable` bool NOT NULL DEFAULT 1, `deleted_at` bigint NOT NULL DEFAULT 0, `name_id` varchar(255) NOT NULL, `name` varchar(255) NOT NULL, `parent_id` varchar(255) NULL, PRIMARY KEY (`id`), INDEX `department_deleted_at` (`deleted_at`), UNIQUE INDEX `department_name_id_deleted_at` (`name_id`, `deleted_at`), INDEX `departments_departments_children` (`parent_id`), CONSTRAINT `departments_departments_children` FOREIGN KEY (`parent_id`) REFERENCES `departments` (`id`) ON UPDATE NO ACTION ON DELETE SET NULL) CHARSET utf8mb4 COLLATE utf8mb4_bin;
